def retrieve_nft(wallet_addr):
    r = requests.get('https://api.ftmscan.com/api', params={
    'module': 'account',
    'action': 'tokennfttx',
    'address': wallet_addr,
    'startblock': 0,
    'endblock': 999999999,
    'sort': 'asc'
    })
    nft_tx = r.json()['result']
    nft_set = set()
    for i in range(len(nft_tx)):
        if nft_tx[i]['tokenSymbol'] != 'RM':
            continue
        if nft_tx[i]['to'] == wallet_addr.lower():
            nft_set.add(nft_tx[i]['tokenID'])
        else:
            nft_set.remove(nft_tx[i]['tokenID'])
    return nft_set

def query_char_info(wallet_addr):
    nft_set = retrieve_nft(wallet_addr)
    char_info = {}
    def _thread(id):
        c = rm_contract.functions['class'](int(id)).call()
        l = rm_contract.functions.level(int(id)).call()
        x = rm_contract.functions.xp(int(id)).call()
        adventure_time = rm_contract.functions.adventurers_log(int(id)).call()
        attr = attr_contract.functions.ability_scores(int(id)).call()
        cellar_time = cellar_contract.functions.adventurers_log(int(id)).call()
        char_info[id] = {
            'class': c, 
            'level': l, 
            'xp': x, 
            'attr': attr, 
            'adventure_time': adventure_time,
            'adventure_time_pass': time.time() > adventure_time,
            'cellar_time': cellar_time,
            'cellar_time_pass': time.time() > cellar_time
        }
    
    threads = []
    for id in nft_set:
        t = threading.Thread(target=_thread, args=(id,))
        t.start()
        threads.append(t)
    for t in threads:
        t.join()
    
    return char_info
    